#include <stdio.h>#include <stdlib.h>#include "../src/percept.h"int main(void){    char key;        CvCapture *input_video;                printf("Welcome to Percept Demo App.");        printf("\nSelect option: \n\n1.  Get Lucas\n2.  Get Lucas FFD\n3.  Show Lucas \n4.  Show Lucas FFD\n\nEnter: ");            scanf("%c",&key);     //   printf("%c",key);    switch (key)    {    case '1':        {                        int maxPoints;            pcMatrixPercept matrix;            printf("Enter max number of feature points:");            scanf("%d",&maxPoints);            printf("\nInit...\n");            initPercept(640,480,"CAMERA",&input_video);                        LucasPercept(2.00, maxPoints, &input_video, &matrix);            if(matrix.getStatus())            {            for (int i=0; i<matrix.getWidth(); i++) {                for (int j=0; j<matrix.getHeight(); j++) {                    if (matrix.getRelDepth(i,j)>10) {                                            printf("-(%d,%d)%d-",i,j,matrix.getRelDepth(i,j));                    }                }            }            }            else            {                fprintf(stderr,"Error! init Matrix.");            }            printf("\nExit...\n");            exitPercept();                        break;        }    case '2':        {            int maxPoints;            pcMatrixPercept matrix;            printf("Enter max number of feature points:");            scanf("%d",&maxPoints);            printf("\nInit...\n");            initPercept(640,480,"CAMERA",&input_video);                        LucasPerceptFFD(5.00, maxPoints, &input_video, &matrix);            if(matrix.getStatus())            {                for (int i=0; i<matrix.getWidth(); i++) {                    for (int j=0; j<matrix.getHeight(); j++) {                        if (matrix.getRelDepth(i,j)>10) {                                                        printf("-(%d,%d)%d-",i,j,matrix.getRelDepth(i,j));                        }                    }                }            }            else            {                fprintf(stderr,"Error! init Matrix.");            }            printf("\nExit...\n");            exitPercept();                        break;        }    case '3':        {            int maxPoints;            printf("Enter max number of feature points:");            scanf("%d",&maxPoints);            printf("\nInit...\n");            initPercept(640,480,"CAMERA",&input_video);                  LucasPerceptShow(30.00, maxPoints,&input_video);                        printf("\nExit...\n");            exitPercept();            break;        }    case '4':        {            int maxPoints;            printf("Enter max number of feature points:");            scanf("%d",&maxPoints);            printf("\nInit...\n");            initPercept(640,480,"CAMERA",&input_video);                        LucasPerceptShowFFD(30.00, maxPoints,&input_video);                        printf("\nExit...\n");            exitPercept();            break;        }        default:        {            printf("No such option. Try again.");        }    }        }