#include <stdio.h>#include <stdlib.h>#include "percept.h"int main(void){    char key;        CvCapture *input_video;        initPercept(640,480,"CAMERA",&input_video);        pcMatrixPercept matrix;        printf("Welcome to Percept Demo App.");        printf("\nSelect option: \n\n1.  Get Lucas\n2.  Show Lucas \n3.  DepthMap\n\nEnter: ");            scanf("%c",&key);     //   printf("%c",key);    switch (key)    {    case '1':        {                        int maxPoints;            printf("Enter max number of feature points:");            scanf("%d",&maxPoints);                                    matrix=LucasPercept(5.00, maxPoints,&input_video);            //matrix=LucasPercept(5.00, maxPoints,&input_video);                       for (int i=0; i<matrix.getWidth(); i++) {                for (int j=0; j<matrix.getHeight(); j++) {                    if (matrix.getRelDepth(i,j)>0) {                                            printf("-(%d,%d)%d-",i,j,matrix.getRelDepth(i,j));                    }                }            }                                    break;        }    case '2':        {            int maxPoints;            printf("Enter max number of feature points:");            scanf("%d",&maxPoints);                        LucasPerceptShow(20.00, maxPoints,&input_video);            //matrix=LucasPercept(5.00, maxPoints,&input_video);                                    break;        }    case '3':        {            printf("Not yet ready.");            break;        }    default:        {            printf("No such option. Try again.");        }    }        exitPercept();}