#include <stdio.h>#include <stdlib.h>#include "lucas.h"int main(void){    char key;    printf("Welcome to Percept Demo App.");    printf("\nSelect option: \n\n1.  Lucas\n2.  DepthMap\n\nEnter: ");            scanf("%c",&key);     //   printf("%c",key);    switch (key)    {    case '1':        {            int maxPoints;            printf("Enter max number of feature points:");                        scanf("%d",&maxPoints);            Lucas(640, 480, 10.00, maxPoints, "CAMERA");            break;        }    case '2':        {            printf("Not yet ready.");            break;        }    default:        {            printf("No such option. Try again.");        }    }}